import React, { useEffect, useState } from 'react';

import axios from "axios";
import { render } from '@testing-library/react';


const Quote = () => {
 const [text, setText] = useState("");
 const [author, setAuthor] = useState("");

const quoteGenApi = async()=>{
    let arrayQuotes = [];
    try{
        const text = await axios.get ("https://api.quotable.io/random");
        arrayQuotes = text;
        
    }catch(error) {
        console.log(error);
    }

  try{
      setText(arrayQuotes.content);
      setAuthor(arrayQuotes.author);
    }catch(error) {
        console.log(error);
  }
};

useEffect(() => {
    quoteGenApi();
}, []);

    render(); {
        return(
            <div>
            {text}{author}
            <button onClick= {quoteGenApi}>Daily Motivation</button>
            </div>
        );
    };
}

export default Quote;
