import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Homes() {
    const [home, updateHome] = useState(null);
  
    async function getHome() {
      try {
        const res = await axios.get('http://localhost:8080/homes');
        setHome(res.data);
      } catch(e) {
        console.error(e, e.message);
      }
    }
  
    useEffect(() => {
      getHome();
    }, [])
  
    const [form, setForm] = useState(null);
  
    function handleChange(e) {
      const { name, value } = e.target;
      setForm({ ...form, [name]: value });
    }
  
    function handleSubmit(e) {
      e.preventDefault();
      createHome();
    }
  
    async function createHome() {
      try {
        const res = await axios.post('http://localhost:8080/homes', form);
        updateHome([...home, res.data]);
      } catch(e) {
        console.error(e, e.message);
      }
    }
  
    // const [updateHome, updatedHome] = useState(null);
  
    function updatedHome(home) {
      setUpdatedHome(home)
    }
  
    function handleEditChange(e) {
      const { name, value } = e.target;
      setUpdatedHome({ ...updatedHome, [name]: value });
    }
  
    async function handleEditSubmit(e) {
      e.preventDefault();
      try {
        const res = await axios.patch('http://localhost:8080/homes', updatedHome);
        console.log(res.data);
        getHome();
      } catch(e) {
        console.error(e, e.message);
      }
    }
  
    async function deleteHome(homeId) {
      try {
        const res = await axios.delete('http://localhost:8080/homes/' + homeId);
        console.log(res.data);
        getHome();
      } catch(e) {
        console.error(e, e.message);
      }
    }
  
    return(
      <div className = "container">
          <h2>Home Inventory</h2>
        { homes && homes.map(home=> <Home home={ home } updateHome={ updateHome } deleteHome={ deleteHome } />)}
  
        <div>
          <h2>Log New Inventory</h2>
          <form
            className="log-new-inventory"
            onChange={ (e) => handleChange(e) }
            onSubmit={ (e) => handleSubmit(e) }>
            <label>
              Category
              <input type="text" name="homeCategory" />
            </label>
            <label>
              Brand
              <input type="text" name="homeBrand" />
            </label>
            <label>
              Location
              <input type="text" name="homeLocation" />
            </label>
            <label>
              Description
              <input type="text" name="description" />
            </label>
            <label>
              Number of Items
              <input type="integer" name="homeNumberofItems" />
              <label>
              In Stock
              <input type="boolean" name="homeInStock" />
            </label>
            </label>
            <input type="submit" value="Log New Inventory" className="update" />
          </form>
  
          { updatedPark && <form
            onChange={ (e) => handleEditChange(e) }
            onSubmit={ (e) => handleEditSubmit(e) }>
            <label>
              Category
              <input type="text" name="homeCategory" defaultValue={ updatedHome.homeCategory } />
            </label>
            <label>
              Brand
              <input type="text" name="homeBrand" defaultValue={ updatedHome.homeBrand } />
            </label>
            <label>
              Location
              <input type="text" name="homeLocation" defaultValue={ updatedHome.homeLocation } />
            </label>
            <label>
              Description
              <input type="text" name="description" defaultValue={ updatedHome.description } />
            </label>
            <label>
              Number of Items
              <input type="text" name="homeNumberofItems" defaultValue={ updatedHome.homeNumberofItems} />
            </label>
            <label>
              In Stock
              <input type="text" name="homeInStock" defaultValue={ updatedHome.homeInStock} />
            </label>
            <input className="update" type="submit" value="Log New Inventory" />
          </form> }
        </div>
      </div>
    )
  }
  
  function Home({ home, updateHome, deleteHome }) {
    return (
        <div className="Homecontainer">
        <h1 className='text shadow'>Home Inventory</h1>

      <div className="home container" key={ home.id }>
        <h2 className="homeCategory">{"home.homeCategory"}</h2>
        <h3 className="homeBrand">{"home.homeBrand"}</h3>
        <h3 className="homeLocation">{"home.homeLocation"}</h3>
        <h3 className="description">{"description"}</h3>
        <h3 className="homeNumberofItems">{"home.homeNumberofItems"}</h3>
        <h3 className="homehomeInStock">{"home.homeInStock"}</h3>
        
         </div>
         

        <button className="update" onClick={ () => updateHome(home) }>Update Home</button>
        <button className="delete" onClick={ () => deleteHome(home.id) }>Delete Home</button>
      </div>
    )
  }
